entry.value:
var flowFile = session.get();
if (flowFile !== null) {

    var StreamCallback = Java.type("org.apache.nifi.processor.io.StreamCallback");
    var IOUtils = Java.type("org.apache.commons.io.IOUtils");
    var StandardCharsets = Java.type("java.nio.charset.StandardCharsets");

    flowFile = session.write(flowFile, new StreamCallback(function(inputStream, outputStream) {

        var inputJSON = IOUtils.toString(inputStream, StandardCharsets.UTF_8);
        var contentObj = JSON.parse(inputJSON);

        //contentObj.date_time_1sec = flowFile.getAttribute("date_time");



		var d_10min=new Date(flowFile.getAttribute("date_time"));
		var d_10sec=new Date(flowFile.getAttribute("date_time"));

		var d_1d=new Date(flowFile.getAttribute("date_time"));
		var d_1h = new Date(flowFile.getAttribute("date_time"));
		var d_1min = new Date(flowFile.getAttribute("date_time"));
		var d_1sec = new Date(flowFile.getAttribute("date_time"));

		d_10min.setMilliseconds(0);
		d_10sec.setMilliseconds(0);

		d_1sec.setMilliseconds(0);
		d_1min.setMilliseconds(0);
		d_1h.setMilliseconds(0);
		d_1d.setMilliseconds(0);

		contentObj.date_time_1sec=d_1sec;		



		contentObj.date_time_10sec=new Date(
		(
		Math.round(d_10sec.getTime() / (1000*10)) * (1000*10)
		)
		+
		(d_10sec.getTimezoneOffset() * 60000)
		);		

		d_1d.setSeconds(0);
		d_1h.setSeconds (0);
		d_1min.setSeconds (0);
		d_1min.setSeconds(0);

		contentObj.date_time_1min=d_1min;		

		contentObj.date_time_10min=new Date(
		(
		Math.round(d_10min.getTime() / (1000*60*10)) * (1000*60*10)
		)
		+(d_10min.getTimezoneOffset() * 60000)
		);		



		d_1h.setMinutes (0);
		d_1d.setMinutes (0);
		contentObj.date_time_1h = d_1h;


		contentObj.date_time_1d= new Date(d_1d.setHours(0));


        outputStream.write(JSON.stringify(contentObj).getBytes(StandardCharsets.UTF_8));
    }));

    session.transfer(flowFile, REL_SUCCESS);
}
